import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import './Sidebar.css';

// Controlled if `collapsed` prop is provided; otherwise fall back to internal state
function Sidebar({ collapsed: collapsedProp, onToggle }) {
  const [localCollapsed, setLocalCollapsed] = useState(false);
  const isControlled = typeof collapsedProp === 'boolean';
  const collapsed = isControlled ? collapsedProp : localCollapsed;

  const toggle = () => {
    if (isControlled) {
      onToggle && onToggle();
    } else {
      setLocalCollapsed((c) => !c);
    }
  };

  return (
    <aside className={`sidebar ${collapsed ? 'collapsed' : ''}`}>
      <div className="sidebar-header">
        <h4 className="mb-2 text-center">{collapsed ? 'TQL' : 'Trang quản lý'}</h4>
        <button className="toggle-btn" onClick={toggle} aria-label="Toggle sidebar">
          {collapsed ? '➡' : '⬅'}
        </button>
      </div>
      <nav>
        <ul className="nav flex-column">
          <li className="nav-item">
            <NavLink
              to="/"
              className={({ isActive }) =>
                `nav-link ${isActive ? 'active' : ''}`
              }
              end
              data-short="H"
            >
              <span className="link-short" aria-hidden>
                H
              </span>
              <span className="link-text">Home</span>
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink
              to="/dashboard"
              className={({ isActive }) =>
                `nav-link ${isActive ? 'active' : ''}`
              }
              data-short="D"
            >
              <span className="link-short" aria-hidden>
                D
              </span>
              <span className="link-text">Dashboard</span>
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink
              to="/about"
              className={({ isActive }) =>
                `nav-link ${isActive ? 'active' : ''}`
              }
              data-short="A"
            >
              <span className="link-short" aria-hidden>
                A
              </span>
              <span className="link-text">About</span>
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink
              to="/contact"
              className={({ isActive }) =>
                `nav-link ${isActive ? 'active' : ''}`
              }
              data-short="C"
            >
              <span className="link-short" aria-hidden>
                C
              </span>
              <span className="link-text">Contact</span>
            </NavLink>
          </li>
        </ul>
      </nav>
    </aside>
  );
}

export default Sidebar;
