import React from 'react';
import './Navbar.css';

const Navbar = ({ collapsed = false }) => {
  const handleLogout = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/logout/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': getCookie('csrftoken'), // nếu CSRF protection đang bật
        },
        credentials: 'include', // gửi cookie session nếu dùng
      });

      const data = await response.json();

      if (response.ok) {
        console.log(data.message); // "Đăng xuất thành công."
        localStorage.removeItem('authToken'); // xóa token local nếu có
        window.location.href = '/login'; // chuyển hướng về trang login
      } else {
        console.error('Logout failed:', data);
      }
    } catch (error) {
      console.error('Error during logout:', error);
    }
  };

  function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let cookie of cookies) {
        const cookieTrim = cookie.trim();
        if (cookieTrim.startsWith(name + '=')) {
          cookieValue = decodeURIComponent(
            cookieTrim.substring(name.length + 1)
          );
          break;
        }
      }
    }
    return cookieValue;
  }

  const leftOffset = collapsed ? '60px' : '220px';
  const width = `calc(100% - ${collapsed ? '60px' : '220px'})`;

  return (
    <nav
      className="navbar"
      style={{
        left: leftOffset,
        width: width,
        transition: 'left 0.25s, width 0.25s',
      }}
    >
      <span className="navbar-brand">Ứng dụng Quản lý</span>
      <button className="logout-button" onClick={handleLogout}>
        Đăng xuất
      </button>
    </nav>
  );
};

export default Navbar;
