import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './HomePage.css'; // dùng lại CSS của HomePage để giữ style

const RegisterPage = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleRegister = async () => {
    if (!username || !email || !password) {
      setError('Vui lòng điền đầy đủ thông tin');
      return;
    }

    try {
      const res = await fetch('http://localhost:8000/api/register/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, email, password }),
      });
      const data = await res.json();

      if (data.status === 'success') {
        navigate('/login'); // quay về trang login sau khi đăng ký
      } else {
        setError(data.message);
      }
    } catch {
      setError('Không thể kết nối server');
    }
  };

  return (
    <div className="home-container">
      <h1 className="home-title">Đăng ký tài khoản</h1>

      <div className="cards-container">
        <div className="card">
          <h2>Thông tin tài khoản</h2>
          <div style={{ marginTop: '12px', width: '100%' }}>
            <input
              type="text"
              placeholder="Tên đăng nhập"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={inputStyle}
            />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              style={inputStyle}
            />
            <input
              type="password"
              placeholder="Mật khẩu"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={inputStyle}
            />
            {error && (
              <div style={{ color: 'red', marginBottom: '8px' }}>{error}</div>
            )}
            <button onClick={handleRegister} style={buttonStyle}>
              Đăng ký
            </button>
            <button
              onClick={() => navigate('/login')}
              style={secondaryButtonStyle}
            >
              Quay lại đăng nhập
            </button>
          </div>
        </div>

        <div className="card">
          <h2>Lợi ích khi đăng ký</h2>
          <p>
            Truy cập đầy đủ dữ liệu, quản lý thông tin cá nhân, và trải nghiệm
            đầy đủ tính năng của hệ thống.
          </p>
        </div>
      </div>
    </div>
  );
};

export default RegisterPage;

// --- Inline style đơn giản cho input & button ---
const inputStyle = {
  width: '100%',
  padding: '8px 12px',
  marginBottom: '12px',
  borderRadius: '6px',
  border: '1px solid #ccc',
  fontSize: '14px',
};

const buttonStyle = {
  width: '100%',
  padding: '10px',
  backgroundColor: '#3498db',
  color: 'white',
  border: 'none',
  borderRadius: '6px',
  cursor: 'pointer',
  fontSize: '16px',
  marginBottom: '8px',
};

const secondaryButtonStyle = {
  ...buttonStyle,
  backgroundColor: 'white',
  color: '#3498db',
  border: '1px solid #3498db',
};
