import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ChangePasswordPage.css';

const ChangePasswordPage = () => {
  const navigate = useNavigate();
  const [oldPassword, setOldPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChangePassword = async () => {
    setError('');
    setSuccess('');

    if (!oldPassword || !newPassword) {
      setError('Vui lòng nhập đầy đủ thông tin');
      return;
    }

    try {
      const res = await fetch('http://localhost:8000/api/me/change-password/', {
        method: 'PATCH', // match với backend
        headers: {
          'Content-Type': 'application/json',
          // nếu CSRF đang bật:
        },
        body: JSON.stringify({
          old_password: oldPassword,
          new_password: newPassword,
        }),
      });

      const data = await res.json();

      if (res.ok) {
        setSuccess(data.message || 'Đổi mật khẩu thành công!');
        setOldPassword('');
        setNewPassword('');
      } else {
        setError(data.errors ? JSON.stringify(data.errors) : data.message);
      }
    } catch (err) {
      setError('Không thể kết nối server');
    }
  };

  function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
      document.cookie.split(';').forEach((cookie) => {
        const [key, value] = cookie.trim().split('=');
        if (key === name) cookieValue = decodeURIComponent(value);
      });
    }
    return cookieValue;
  }

  return (
    <div className="change-container">
      <div className="change-box">
        <h3>Đổi mật khẩu</h3>

        <input
          type="password"
          className="form-control"
          placeholder="Mật khẩu cũ"
          value={oldPassword}
          onChange={(e) => setOldPassword(e.target.value)}
        />

        <input
          type="password"
          className="form-control"
          placeholder="Mật khẩu mới"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
        />

        {error && <div className="text-danger">{error}</div>}
        {success && <div className="text-success">{success}</div>}

        <button
          className="btn btn-primary w-100 mb-2"
          onClick={handleChangePassword}
        >
          Đổi mật khẩu
        </button>

        <button
          className="btn btn-outline-secondary w-100"
          onClick={() => navigate('/')}
        >
          Quay lại trang chủ
        </button>
      </div>
    </div>
  );
};

export default ChangePasswordPage;
